import React, { Component } from "react";
import {
  Text,
  StyleSheet,
  View,
  Image,
  FlatList,
  ImageBackground,
  TouchableOpacity,
} from "react-native";
import StarRating from "react-native-star-rating";
import color from "../../../constants/color";
import { useNavigation } from "@react-navigation/native";
import { useDispatch, useSelector } from "react-redux";
import { wishAdd, wishDel } from "../../../store/products";

// import Segmented from './Segmented';

function BestSelling() {
  const data = useSelector((state) => state.products);

  const dispatch = useDispatch();

  const wishlist = (param, id) => {
    if (param) {
      dispatch(wishDel({ id }));
    } else {
      dispatch(wishAdd({ id }));
    }
  };

  const newData = data.filter(function (el) {
    return el.bestSeller;
  });
  const navigation = useNavigation();

  return (
    <View>
      <View style={{ marginBottom: 25 }}>
        <FlatList
          data={newData}
          horizontal={true}
          renderItem={({ item, index }) => (
            <View style={styles.flatlistView1}>
              <ImageBackground style={styles.img4b} source={item.pic}>
                <View
                  style={{
                    flexDirection: "row",
                    justifyContent: "flex-end",
                    margin: 10,
                  }}
                >
                  <TouchableOpacity
                    onPress={() => wishlist(item.wishlist, item.id)}
                  >
                    {item.wishlist ? (
                      <Image
                        source={require("../../images/heart.png")}
                        style={styles.heart}
                      ></Image>
                    ) : (
                      <Image
                        source={require("../../images/heart.png")}
                        style={styles.heart1}
                      ></Image>
                    )}
                  </TouchableOpacity>
                </View>

                <View style={styles.view4b}>
                  <Text style={styles.txt4b}>{item.title}</Text>
                  <View
                    style={{
                      width: "10%",
                      marginTop: 4,
                      resizeMode: "contain",
                      marginLeft: 15,
                    }}
                  >
                    <StarRating
                      disabled={false}
                      maxStars={5}
                      rating={item.stars}
                      fullStarColor={color.star}
                      starSize={15}
                      starStyle={{}}
                    />
                  </View>
                </View>
              </ImageBackground>

              <View style={{ flexDirection: "row" }}>
                <Text style={styles.txt3b}>${item.price}</Text>
                <TouchableOpacity
                  onPress={() =>
                    navigation.navigate("Product", { itemID: item.id })
                  }
                >
                  <Image
                    style={styles.img5b}
                    source={require("../../images/for.png")}
                  ></Image>
                </TouchableOpacity>
              </View>
            </View>
          )}
          keyExtractor={(item) => item.id}
        />
      </View>
    </View>
  );
}
export default BestSelling;

const styles = StyleSheet.create({
  heart: {
    width: 20,
    height: 25,
    resizeMode: "contain",
    tintColor: color.red,
  },
  heart1: {
    width: 20,
    height: 25,
    resizeMode: "contain",
    tintColor: color.grey,
  },
  flatlistView: {
    marginBottom: 25,
  },
  tabStyle: {
    borderColor: color.white,
  },
  activeTab: {
    borderWidth: 1,
    backgroundColor: color.lightBlue,
    flex: 1,
    elevation: 20,
    borderRadius: 8,
  },
  activeTabText: {
    color: color.black,
    fontWeight: "bold",
  },
  tabTextStyle: {
    color: color.black,
  },
  flatlistView1: {
    // width: '70%',
    alignSelf: "center",
    borderColor: color.border,
    borderWidth: 1,
    backgroundColor: color.white,
    borderRadius: 15,
    flex: 2,
    elevation: 8,
    marginTop: 30,
    margin: 15,
  },
  img3b: {
    width: 25,
    height: 30,
    resizeMode: "contain",
    alignSelf: "center",
  },
  txt4b: {
    fontSize: 14,
    paddingLeft: 15,
    fontWeight: "bold",
  },
  img5b: {
    width: 35,
    height: 40,

    alignSelf: "center",
  },
  img4b: {
    width: 150,
    height: 220,
    resizeMode: "contain",
    alignSelf: "center",
    // marginTop: -40,
    flex: 1,
  },
  view4b: {
    marginTop: 150,
  },
  txt3b: {
    fontSize: 16,
    paddingLeft: 15,
    alignSelf: "center",
    width: "70%",
    fontWeight: "bold",
    color: color.darkBlue,
  },
});
